  (function () {
    var ctx = ($json && $json.data) ? $json : ($item ? $item(0).$json : {});
    var data = (ctx && ctx.data) ? ctx.data : {};

    var content = (data.content || '').replace(/(^|\s)(@it bot|xb)\b/gi, '').trim();
    var quoteMsg = (data.quote && data.quote.msg) ? data.quote.msg : '';
    var attachRaw = (data.quote && data.quote.attach) ? data.quote.attach : '';

    function safeParse(v) {
      if (!v) return {};
      if (typeof v !== 'string') return v;
      try { return JSON.parse(v); } catch (e) { return {}; }
    }

    var a = safeParse(attachRaw);
    var p = safeParse(a.params);

    var candidates = [
      a.hdUrl, a.normalUrl, a.oriUrl, a.href, a.thumbUrl, a.thumb,
      (p && (p.hd || p.rawUrl))
    ];

    var img = '';
    for (var i = 0; i < candidates.length; i++) {
      var u = candidates[i];
      if (typeof u === 'string' && /^https?:\/\//i.test(u)) { img = u; break; }
    }

    if (!img && typeof attachRaw === 'string') {
      var m = attachRaw.match(/https?:\/\/\S+?\.(?:jpg|jpeg|png|gif|webp)/i);
      img = m ? m[0] : '';
    }

    return [content, quoteMsg, img].filter(Boolean).join(' ').trim();
  })()
}}

